and a.is_delete = 0 AND a.client_id = #{clientId} 
AND s.is_delete = 0 AND s.client_id = #{clientId}
AND d.is_delete = 0

	and a.bank_acct = #{bankAcct}


	and (s.store_name like CONCAT('%',#{keyWord},'%') or s.store_No = #{keyWord})


	and a.acct_holder = #{acctHolder}


