a.gift,a.gift_price,a.other_gift,a.other_price,a.options_of_invest,a.val_of_shelf,a.other_invest,a.has_invoice, a.invoice_type,a.other_invoice,a.license_type,a.acct_type,a.bank_name,a.bank_acct,a.acct_holder, w.month_id,w.num_of_gift,w.num_of_member,w.rate_of_review,w.num1_of_unqualified,w.num2_of_unqualified,w.num_of_verification,
ifnull(w.num_of_verification,0)*100 + ifnull(w.num_of_gift,0)*30 as amount, if(w.`status`=0,'待确认','已确认') as `status`, if(w.`status`=0,'待确认','已确认') as `status2`, if(w.`status`=3,'核销完毕','确认核销') as `status3`
from wyeth_contract a INNER JOIN store b on a.store_id = b.store_id LEFT JOIN channel c on c.channel_id = b.channel_id and c.is_delete = 0 and c.client_id = #{clientId} LEFT JOIN `chain` d on d.chain_id = b.chain_id and d.is_delete = 0 and d.client_id = #{clientId}
LEFT JOIN client_structure s on s.client_structure_id = b.client_structure_id and s.is_delete = 0 and s.client_id = #{clientId} LEFT JOIN client_structure n ON n.client_structure_id =s.parent_id AND n.is_delete=0 AND n.client_id=#{clientId}
LEFT JOIN wyeth_contract_feedback w on w.contract_id = a.contract_id and w.is_delete= 0 LEFT JOIN wyeth_contract_detail i on i.contract_id = a.contract_id and i.is_delete = 0
where a.is_delete = 0 and a.client_id = #{clientId} and b.is_delete = 0 and b.client_id = #{clientId} AND (FIND_IN_SET(b.client_structure_id,#{subStructureId})>0)
and b.store_no like CONCAT('%',#{storeNo},'%')
and b.store_name like CONCAT('%',#{storeName},'%')
and b.city_id = #{cityId}
and b.client_structure_id = #{structureId}
and DATE_FORMAT(a.sign_date,'%Y-%m-%d') = #{signDate}
and a.start_date >= #{startDate}
and a.end_date <= #{endDate}
group by b.store_no,province,city,b.store_name,c.channel_name,d.chain_name,a.sign_date,a.start_date,a.end_date, a.gift,a.gift_price,a.other_gift,a.other_price,a.options_of_invest,a.val_of_shelf,a.other_invest,a.has_invoice, a.invoice_type,a.other_invoice,a.license_type,a.acct_type,a.bank_name,a.bank_acct,a.acct_holder, w.month_id,w.num_of_gift,w.num_of_member,w.rate_of_review,w.num1_of_unqualified,w.num2_of_unqualified,w.num_of_verification, amount,`status`,`status2`,`status3`

select
	a.contract_id,s.structure_name,a.contract_pic,a.client_id,b.store_no,e.province,f.city,b.store_name,c.channel_name,d.chain_name,a.sign_date,a.start_date,a.end_date,a.*
	from wyeth_contract a INNER JOIN store b on a.store_id = b.store_id
	LEFT JOIN channel c on c.channel_id = b.channel_id and c.is_delete = 0 and c.client_id = #{clientId}
	LEFT JOIN `chain` d on d.chain_id = b.chain_id and d.is_delete = 0 and d.client_id = #{clientId}
	LEFT JOIN province e on b.province_id = e.province_id and e.is_delete = 0
	LEFT JOIN city f on f.city_id = b.city_id
	LEFT JOIN client_structure s on s.client_structure_id = b.client_structure_id and s.is_delete = 0 and s.client_id = #{clientId}
	where a.is_delete = 0 and a.client_id = #{clientId}
	and b.is_delete = 0 and b.client_id = #{clientId}
	AND contract_id=#{contractId}

SELECT d.invoice_pic,d.month_id, a.client_id,d.feedback_id,a.contract_id,s.store_no,s.store_name,cs.structure_name,city.city,a.has_invoice,a.invoice_type,a.acct_type,a.bank_name,a.bank_acct,a.acct_holder
,a.sign_date,d.num_of_gift,d.num_of_member,d.rate_of_review,d.num1_of_unqualified,d.num2_of_unqualified,d.num_of_verification,d.`status`
FROM wyeth_contract a INNER JOIN store s ON s.store_id = a.store_id
left join client_structure cs on cs.client_structure_id = s.client_structure_id
LEFT JOIN city on s.city_id = city.city_id
INNER JOIN wyeth_contract_feedback d ON d.contract_id = a.contract_id
WHERE a.is_delete = 0 AND a.client_id = #{clientId} 
AND s.is_delete = 0 AND s.client_id = #{clientId}
AND d.is_delete = 0 AND feedback_id=#{feedbackId}

